package lec1;

public class Marks {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int marks = 30;
		
		if(marks >= 85)
		{
			System.out.println("Grade: A");
		}
		else if (marks >= 65)
		{
			System.out.println("Grade: B");
		}
		else if(marks >= 45)
		{
			System.out.println("Grade: C");
		}
		else
		{
			System.out.println("Grade: fail");
		}

	}

}
